import React, { Component } from 'react';
import './Hello.css'

class Hello extends Component{
    //always want to have a render function
    //JSX and not actual HTML tags to create the virtual DOM
    //this hello object has properties greeting
    //now we have access to whatever property and whatever property value we get from Hello
    render() {
        return (
        <div className='f1 tc'>
            <h1> Hello World </h1>
            <p>{this.props.greeting}</p>
        </div>
        );
    }
}
//we put parenthesis around return statement to evaluate as an expression
export default Hello;
//we have to export it to let another file use it
//we use default when we're exporting one thing
